package com.chm006.library.utils;import android.app.Activity;import android.content.ComponentName;import android.content.Context;import android.content.Intent;import android.location.LocationManager;import android.net.ConnectivityManager;import android.net.NetworkInfo;import android.telephony.TelephonyManager;import java.util.List;public class NetworkUtil {    private NetworkUtil() {        /* cannot be instantiated */        throw new UnsupportedOperationException("cannot be instantiated");    }    /**     * 判断网络是否连接     *     * @param context     * @return     */    public static boolean isConnected(Context context) {        ConnectivityManager cm = (ConnectivityManager) context                .getSystemService(Context.CONNECTIVITY_SERVICE);        if (null != cm) {            NetworkInfo networkInfo = cm.getActiveNetworkInfo();            if (networkInfo != null && networkInfo.isConnected()) {                if (networkInfo.getState() == NetworkInfo.State.CONNECTING) {                    return true;                }            }        }        return false;    }    /**     * GPS是否打开     *     * @param context     * @return     */    public static boolean isGpsEnabled(Context context) {        LocationManager locationManager = ((LocationManager) context.getSystemService(Context.LOCATION_SERVICE));        List<String> accessibleProviders = locationManager.getProviders(true);        return accessibleProviders != null && accessibleProviders.size() > 0;    }    /**     * WiFi是否打开     *     * @param context     * @return     */    public static boolean isWifiEnabled(Context context) {        ConnectivityManager mgrConn = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);        TelephonyManager mgrTel = (TelephonyManager) context.getSystemService(Context.TELEPHONY_SERVICE);        return ((mgrConn.getActiveNetworkInfo() != null && mgrConn.getActiveNetworkInfo().getState() == NetworkInfo.State.CONNECTED) || mgrTel                .getNetworkType() == TelephonyManager.NETWORK_TYPE_UMTS);    }    /**     * 判断是否是WIFI连接     *     * @param context     * @return     */    public static boolean isWifi(Context context) {        ConnectivityManager connMgr = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);        NetworkInfo activeNetInfo = connMgr.getActiveNetworkInfo();        if (activeNetInfo != null && activeNetInfo.getType() == ConnectivityManager.TYPE_WIFI) {            return true;        }        return false;    }    /**     * 判断是否是3G连接     *     * @param context     * @return     */    public static boolean is3G(Context context) {        ConnectivityManager connMgr = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);        NetworkInfo activeNetInfo = connMgr.getActiveNetworkInfo();        if (activeNetInfo != null && activeNetInfo.getType() == ConnectivityManager.TYPE_MOBILE) {            return true;        }        return false;    }    /**     * 打开网络设置界面     */    public static void openSetting(Activity activity) {        Intent intent = new Intent("/");        ComponentName cm = new ComponentName("com.android.settings",                "com.android.settings.WirelessSettings");        intent.setComponent(cm);        intent.setAction("android.intent.action.VIEW");        activity.startActivityForResult(intent, 0);    }}